import React, {useState} from "react";

const MovieContext=React.createContext({});
const Movieprovider=({childern}) => {
    const [movies,setMovies]=useState([]);
    return(
        <MovieContext.Provider
        value={{
            movies,
            setMovies,
        }}
        >
            {childern}
        </MovieContext.Provider>

    );
};
export  default MovieContext;
export {Movieprovider};
