import { useEffect, useState } from "react";
import {View, Text, StyleSheet, Image, ActivityIndicator} from "react-native";
import { FlatList } from "react-native-web";

// Default component that will get called by app.js
// Prop "navigation" is added through app.js when using navigation component.
export default function Home({ navigation }) {
  // A state holding all the movie data.
  const [data, setData] = useState([]);
    const [currentPage, setCurrentPage] = useState([1]);
  // Fetch movie list when component is mounted
  useEffect(() => {
    console.log(process.env);
    fetchMovies();
  }, []);

  // Function to fetch movie list
  function fetchMovies() {
    //https://api.themoviedb.org/3/movie/popular?api_key=3948d77682e5858510c087b43a9399c6&language=en-US&page=1
    fetch(`https://api.themoviedb.org/3/movie/popular?api_key=3948d77682e5858510c087b43a9399c6&language=en-US&page=1`
    )
      .then((response) => response.json())
      .then((data) => setData(data.results));
  }
    const renderLoader = () => {
        return(
            <View style={styles.styleLoader}>
                <ActivityIndicator size="large" color="red"/>
            </View>
        );
    };
    const loadMoreItem = () => {
        setCurrentPage(currentPage+1);

    };
  // Render function that returns the Item component
  const renderItem = ({ item }) => {
    return(
        <View style={styles.container}>
            <Image style={styles.itemImagesStyle}  source={{ uri: `https://image.tmdb.org/t/p/original${item.poster_path}`,}}/>
          <Item navigation={navigation} movieId={item.id} title={item.title} />
        </View>

    );

  }
  // returns a flatlist because we only need to render what the user can see.
  // Else it would be too heavy when we load alot of movies.
  return (
    <View style={styles.container}>
      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      /*  ListFooterComponent={renderLoader}
        onEndReached={loadMoreItem}*/

      />

    </View>
  );
}

const Item = ({ navigation, title, movieId }) => (
  <View style={styles.item}>
    <Text
      onPress={() =>
        navigation.navigate("Details", {
          movieId,
        })
      }
      style={styles.title}
    >
        <View style={[styles.container1, styles.horizontal]}>
            <ActivityIndicator size="large" color="#00ff00" />
        </View>
      {title}
    </Text>
  </View>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    justifyContent: "center",
  },
  itemImagesStyle:{
    width:100,
    height:100,
    marginRight:16,

  },
    styleLoader:{
        marginVertical:16,
        alignItems:"center"
    },
  item: {
    backgroundColor: "#C2FFCEFF",
    padding: 10,
    marginVertical: 8,
    marginHorizontal: 10,
  },
  title: {
    fontSize: 32,
  },
    container1: {
        flex: 1,
        justifyContent: "center"
    },
    horizontal: {
        flexDirection: "row",
        justifyContent: "space-around",
        padding: 10
    }
});
